ue="<? print($current_time); ?>" size="20" maxlength="40">      <INPUT type="button" value="Go" onClick="verify()">    </TD>  </TR>  <TR class="lieux_top">    <TD colspan="2" nowrap>      <B>Liste des lieux</B>    </TD>    <TD align="center">      <B>T&acirc;ches</B>    </TD>    <TD align="center">      <B>Orgas</B>    </TD>  </TR><?  $lieux_array=result_lieux($req_lieux_taches);  $orgas_array=result_orgas($req_orgas);  $current_lieu="";  $current_tache="";  for ($i=0;$i<count($lieux_array);$i++)  {    $nom="";    $titre="";	$nom_orgas="";    if (($current_lieu!=$lieux_array[$i]->nom) || ($current_tache!=$lieux_array[$i]->titre))    {      $nom=$lieux_array[$i]->nom;      $titre=$lieux_array[$i]->titre;      $current_lieu=$nom;      $current_tache=$titre;      $list_orgas=explode("|",$lieux_array[$i]->id_orga);      for ($j=0;$j<count($list_orgas);$j++)      {        for ($k=0;$k<count($orgas_array);$k++)        {          if ($orgas_array[$k]->id_orga==$list_orgas[$j])          {		    if ($nom_orgas!="") $nom_orgas=$nom_orgas."<BR>";		    $nom_orgas=$nom_orgas.$orgas_array[$k]->nom_orga;            break;          }        }      }    }?>  <TR class="lieux">      <TD width="1%">&nbsp;&nbsp;&nbsp;</TD>    <TD valign="top" nowrap>      <? print($nom); ?>    </TD>    <TD align="center" valign="top" nowrap>      <? print($titre); ?>    </TD>    <TD align="center" nowrap>      <? print($nom_orgas); ?>    </TD>  </TR><?  }?>  <TR class="lieux_top">    <TD colspan="2" nowrap>      <B>Liste des lieux</B>    </TD>    <TD align="center">      <B>Activit&eacute;</B>    </TD>    <TD align="center">      <B>Equipes</B>    </TD>  </TR><?  $lieux_array=result_lieux($req_lieux_activites);  $equipes_array=result_equipes($req_equipes);  $current_lieu="";  $current_activite="";  for ($i=0;$i<count($lieux_array);$i++)  {    $nom="";    $titre="";	$nom_equipes="";    if (($current_lieu!=$lieux_array[$i]->nom) || ($current_activite!=$lieux_array[$i]->titre))    {      $nom=$lieux_array[